INSERT INTO `faqs` (`question`, `answer`, `category`, `route_name`, `is_active`)
VALUES
  ('Can a private limited company take a loan from an individual?', 'Yes, a private limited company can take a loan from an individual, subject to compliance with the provisions of the companies act, 2013 and other regulatory requirements. however, there are certain restrictions and procedures that must be followed', 'Private Limited', ',global,/private-limited-company-registration,', 1),
  ('Are private limited companies listed on the stock exchange?', 'No, private limited companies are not listed on the stock exchange. they cannot issue shares to the public and are restricted in terms of transferring shares, unlike public companies.', 'Private Limited', ',global,/private-limited-company-registration,', 1),
  ('How do private limited companies raise funds?', 'Private limited companies raise funds through equity investments from shareholders, venture capitalists, private equity, or by taking loans from banks and financial institutions.', 'Private Limited', ',global,/private-limited-company-registration,', 1),
  ('What is a private limited company?', 'A private limited company is a business entity privately held by shareholders, where ownership is limited to a small group of investors. the shares are not available for public trading, and there are restrictions on the transfer of shares.', 'Private Limited', ',global,/private-limited-company-registration,', 1),
  ('What are the benefits of registering a private limited company in india?', 'Registering a private limited company in india offers benefits like limited liability for shareholders, ease of raising funds, perpetual succession, separate legal entity, and credibility with financial institutions and investors.', 'Private Limited', ',global,/private-limited-company-registration,', 1),
  ('Why choose a private limited company structure?', 'Choosing a private limited company structure provides advantages like limited liability protection, ease of raising capital, tax benefits, and the flexibility to grow the business without extensive compliance requirements faced by public companies.', 'Private Limited', ',global,/private-limited-company-registration,', 1),
  ('Is a private limited company a corporation?', 'Yes, a private limited company is a type of corporation, but it is privately held and not traded on public stock exchanges. it''s a separate legal entity from its owners and has limited liability.', 'Private Limited', ',global,/private-limited-company-registration,', 1);
